mapper.select:
select distinct shopname from tblshop s
inner join tblshoppingcard sc on sc.shopid = s.shopid
where sc.userid = #{userid}

select sc.*,s.shopname,g.goodsname,g.goodsprice,g.goodsimg,g.goodsnum from tblshoppingcard sc
inner join tblgoods g on g.goodsid = sc.goodsid
inner join tblshop s on s.shopid = g.shopid
where sc.userid = #{userid}

    and s.shopname in

        #{shopname}

select sc.userid,g.goodsid,g.goodsname,sp.specname from tblshoppingcard sc inner join tblgoods g on g.goodsid = sc.goodsid inner join tblspec sp on sp.goodsid = g.goodsid where sc.userid = #{userid} and sp.parentid != 0
and sc.goodsid in
#{goodsid}
and sp.specid in
#{specid}
